var MeetingDAO = require('./internal/dao');
var MeetingCALL = require('./internal/call');

var createMeeting = module.exports.createMeeting = function(displayName, description, welcomemessage,callback){

	MeetingDAO.createMeeting(displayName, description, welcomemessage, function(err,data){
		if (err) {
			return callback(err);
		}
		return callback(null, data);
	});

};

var getMeetings = module.exports.getMeetings = function(callback){
	MeetingDAO.getMeetings(function(err,data){
		if(err){
			return callback(err);
		}
		
		return callback(null,data);
	});
};

var createBBBCall = module.exports.createBBBCall = function(meetingID, name, callback){
	
	MeetingCALL.createBBBCall(meetingID, name, function(err,data){
		if(err){
			
			return callback(err);
		}
		return callback(null,data);
	});
};