var Cassandra = require('oae-util/lib/cassandra');

var MeetingAPI = require('./api');

console.log("=====================creating meeting columnfamily=======================");
module.exports = function(config, callback) {
    _ensureSchema(function(err) {
        if (err) {
            return callback(err);
        }
       return callback(null);
    });
};


var _ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'Meeting': "CREATE TABLE 'Meeting' ('id' text PRIMARY KEY, 'displayName' text, 'description' text, 'welcomeMessage' text)"
    }, callback);
};